// PID algorithm
// Justin Angevaare
// May 2018 - Nov 2020

// Set temperature
//msg.temperature =  global.get('temp-BK')
msg.temperature = msg.payload;

// Set output max
msg.max_output = 100

// Determine update interval
// Will be NaN if first iteration
msg.epochseconds = new Date() / 1000
msg.interval = msg.epochseconds - flow.get('last_calc_time')
flow.set('last_calc_time', msg.epochseconds)

// Calculate error
msg.target = flow.get('bk_target')
msg.error = msg.target - msg.temperature;

// Calculate proportional action
msg.output = msg.error * flow.get('p');

// Update integrator and calculate integral action
// Skip for first iteration
if(!isNaN(msg.interval)){
    // Update integrator
    // For purposes of integrator, bound error by [-1, 1]
    msg.integrator = flow.get('integrator');
    msg.integrator += msg.interval * Math.max(Math.min(msg.error, 1), -1);

    // Bound total integrator by absolute maximum
    msg.integrator = Math.max(Math.min(msg.integrator, flow.get('integrator_max')), -flow.get('integrator_max'));

    // Output updated integrator to flow context
    flow.set('integrator', msg.integrator);

    // Calculate intergral action
    msg.output += msg.integrator * flow.get('i');
}

// Calculate derivative action
// Skip for first iteration
if(!isNaN(msg.interval)){
    msg.derivative = (msg.error - flow.get('last_error'))/msg.interval;
    msg.output += msg.derivative * flow.get('d');
}

// Output updated last_error to flow context
msg.last_error = msg.error;
flow.set('last_error', msg.last_error);

// Bound output by [0, max_output]
msg.output = Math.round(Math.max(Math.min(msg.output, msg.max_output), 0));

// Output element output to flow context
flow.set('output', msg.output);

// Set payload to element output
msg.payload = msg.output;
return msg;

